get_url_key.js <pre><code>function get_url_key(url) {
  // 解析url携带的参数信息
  var params = {};
  try{
    var urls = url.split("?");           
    var arr = urls[1].split("&amp;");              
    for (var i = 0, l = arr.length; i &lt; l; i++) {
      var a = arr[i].split("=");              
      params[a[0]] = a[1];                  
    }

  }catch(e){}
  return params;
}

